import React from 'react';
import { Image,View, Text, StyleSheet, TouchableOpacity } from 'react-native';

export default function PaymentSuccessScreen() {
    return (
        <View style={styles.container}>
               <View style={[styles.topAppBar, styles.topAppBarLayout]}>
        <View style={[styles.headerIcon, styles.headerIconFlexBox]}>
          <Image
            style={styles.iconLayout}
            contentFit="cover"
            source={require("../assets/arrow-left.png")}
          />
          <Text style={[styles.title, styles.titleTypo]}>Payment method</Text>
        </View>
        <Image
          style={[styles.moreVerticalIcon, styles.iconLayout]}
          contentFit="cover"
          source={require("../assets/more-vertical.png")}
        />
      </View>
            <View style={styles.stepsContainer}>
                <Text>Details</Text>
                <Text style={styles.activeStep}>Payment</Text>
                <Text>Confirm</Text>
            </View>
            <View style={styles.contentContainer}>
            <Image
          style={styles.progressIndicatorIcon}
          contentFit="cover"
          source={require("../assets/sucess-sign.png")}
        />
                <Text style={styles.successText}>Success!</Text>
                <Text style={styles.descriptionText}>
                    Great purchase. Thank you for your purchase
                </Text>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Done</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#1F1F29',
    },
    topAppBarLayout: {
        height: 48,
        left: 0,
      },
      headerIconFlexBox: {
        paddingVertical: 5,
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
        position: "absolute",
      },
      titleTypo: {
        textAlign: "left",
        fontFamily: 2,
        color: "White",
      },
      iconLayout: {
        height: 24,
        width: 24,
        overflow: "hidden",
      },
    headerText: {
        fontSize: 18,
        color: 'white',
    },
    stepsContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginBottom: 50,
    },
    activeStep: {
        color: 'cyan',
    },
    contentContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    iconContainer: {
        width: 100,
        height: 100,
        borderRadius: 50,
        backgroundColor: 'green',
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 20,
    },
    icon: {
        fontSize: 50,
        color: 'white',
    },
    successText: {
        fontSize: 24,
        color: 'white',
        marginBottom: 10,
    },
    descriptionText: {
        color: 'lightgray',
        marginBottom: 30,
    },
    button: {
        paddingVertical: 10,
        paddingHorizontal: 20,
        borderRadius: 81,
        backgroundColor: '#686DCD',
    },
    buttonText: {
        color: 'white',
    },
});
